import { ImageEncoderImpl } from './ImageEncoderImpl'
import { Base64 } from '../domain/entity/Base64'

describe('ImageEncoderImpl', () => {
  const imageEncoderImpl = new ImageEncoderImpl('./tmp')

  describe('base64Encode', () => {
    it('pngファイルをbase64エンコードする', async () => {
      const encoded = (await imageEncoderImpl.base64Encode(
        './resource/coupon_image.png'
      )) as string
      expect(typeof encoded).toBe('string')
    })

    it('jpgファイルをbase64エンコードする', async () => {
      const encoded = (await imageEncoderImpl.base64Encode(
        './resource/coupon_qr_code.jpg'
      )) as string
      expect(typeof encoded).toBe('string')
    })
  })

  describe('base64Decode', () => {
    const pngBase64 =
      ''
    const jpgBase64 =
      ''

    it('pngファイルbase64データをデコードする', async () => {
      const decoded = await imageEncoderImpl.base64Decode(new Base64(pngBase64))
      expect(decoded.file instanceof Buffer).toBe(true)
      expect(decoded.ext).toBe('png')
    })

    it('jpgファイルbase64データをデコードする', async () => {
      const decoded = await imageEncoderImpl.base64Decode(new Base64(jpgBase64))
      expect(decoded.file instanceof Buffer).toBe(true)
      expect(decoded.ext).toBe('jpg')
    })
  })
})
